mapper.update:
UPDATE tbl_reply
   SET replyText = #{replyText}, updatedate = now()
 WHERE rno = #{rno}

mapper.select:
SELECT *
  FROM tbl_reply
 WHERE bno       = #{bno}
 ORDER BY rno DESC
 LIMIT #{criteria.pageStart}, #{criteria.perPageCount}

SELECT count(bno)
  FROM tbl_reply
 WHERE bno       = #{bno}

SELECT bno
  FROM tbl_reply
 WHERE rno       = #{rno}
